update.foreach:
update clear_result_summary

  plat_no = #{item.plat_no}
  ,recharge_count_plat=#{item.recharge_count_plat}
  ,recharge_sum_plat=#{item.recharge_sum_plat}
  ,withdrawals_count_plat=#{item.withdrawals_count_plat}
  ,withdrawals_sum_plat=#{item.withdrawals_sum_plat},

  ,balance_bank=#{item.balance_bank}
  ,balance_sum=#{item.balance_sum}

WHERE plat_no = #{item.plat_no} and DATE_FORMAT(clear_date,'%Y%m%d') = #{item.clear_date};
